import base64, struct
#判断并获取位图的信息
bmp_data = base64.b64decode('')
def bmp_info(data):
    if len(data)>30:
        data=data[:30]
    data=struct.unpack('<ccIIIIIIHH',data)
    if data[0]+data[1] == b'BM':
        return {'width': data[6],'height': data[7],'color': data[9]}
    else:
        return 'not bit map'
print(bmp_info(bmp_data))